sp_angular_provider.script:
function accountActivationCheckSubmitterScoped($http){

	this.checkSubmitter = function(piname, sessionId){
		var data;
		$.ajax({
			dataType: "json",
			type: "GET",
			url: '/api/x_naci_account_act/accountactivationscoped/checkSubmitter',
			data: {name: piname, desc: 'CBIIT Account Activation Application'},
			beforeSend: function(xhr){ xhr.setRequestHeader('X-UserToken', sessionId); },
			async: false,
			success: function(response){
				data = response.result;
			},
			error: function(data){
				alert("ERROR");
			}
		});	
		return data;
	}
}
