sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {
	   var jsonObj = {};
	   var data = request.body.data;
       var request_id = data.request_id;
       var comments = data.approval_comments;
       var action= data.action;
       var role = data.role;

       var gr1 = new GlideRecord("x_naci_account_act_account_activation_table");
       gr1.addQuery("sys_id", request_id);
       gr1.query();
       gr1.next();
	   if(role == 'Direct Supervisor'){
		   gr1.ds_approval_status=action;
	   }else if(role == 'Scientific Directors / Signing Official'){
		   gr1.sdso_approval_status=action;
	   }
       gr1.comments = '(startAction)' + action + '(endAction)(startComments)' +comments + '(endComments)(role)' + role + '(ENDROLE)' + gs.getUser().getFirstName() + ' ' + gs.getUser().getLastName() + 'ENDCOMMENTS';
       gr1.update();

	   jsonObj = {
		   status : data
	   };

	  return jsonObj;
})(request, response);
